            postHTML = '<div class="btemplates-portfolio"><div class="row no-gutters">' + postHTML + '</div></div>';
        } else if (postsStyle === 'course') {
            postHTML = '<hr class="title-separator mb-4"/>' +
                '<div class="btemplates-courses">' +
