    //Back to top
    $j(window).scroll(function () {
        $j(this).scrollTop() > 400 ? $j("#back-top").fadeIn() : $j("#back-top").fadeOut()
    });
    $j("#back-top").click(function () {
        return $j("body,html").animate({scrollTop: 0}, 800), !1
    });

